workunit.code:

import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.common.*;
import com.rameses.rcp.framework.ClientContext;

public class MarketCollectionTypeLookupModel {

    @Service("LOVService")
    def lovSvc;

    String title;
    def selectedItem;
    def onselect;

    def listHandler = [
        getColumnList: {
            return [
                [name:'objid', caption:'']
            ];
        },
        isMultiSelect: {
            return true;
        },
        fetchList: {
            return lovSvc.get("MARKET_COLLECTION_OPTIONS").collect{ [objid: it.key] };
        }
    ] as BasicListModel;


    def doOk() {
        if(!onselect) throw new Exception("Please specify onselect" );
        if( !listHandler.isMultiSelect() ) {
            onselect( selectedItem );
        }
        else {
            onselect( listHandler.selectedValue );
        }
        return "_close";
    }

    def doCancel() {
        return "_close";
    }
}
